mapper.insert:

	SELECT NVL(MAX(SEQ), 0) + 1 SEQ 
	  FROM TB_BOARD
	 WHERE CODE = #{code}

INSERT INTO TB_BOARD (CODE
                    , SEQ
                    , GRP_SEQ
                    , SUBJECT
                    , MEMO
                    , OPEN_YN
                    , CREATE_USER_ID
                    , CREATE_DT
                    , CREATE_IP
                    )
     VALUES (#{code}
           , #{seq}
           , #{seq}
           , #{subject}
           , #{memo}
           , #{openYn}
           , #{createUserId}
           , SYSDATE
           , #{createIp}
           )

mapper.delete:
DELETE FROM TB_BOARD
 WHERE CODE = #{code} 
   AND SEQ = #{seq}

mapper.update:
UPDATE TB_BOARD SET 
		SUBJECT = #{subject}
	  , MEMO = #{memo}
	  , OPEN_YN = #{openYn}
	  , UPDATE_USER_ID = #{updateUserId}
      , UPDATE_DT = SYSDATE
      , UPDATE_IP = #{updateIp}
 WHERE CODE = #{code} 
   AND SEQ = #{seq}

UPDATE TB_BOARD SET
		READCNT = READCNT + 1
 WHERE CODE = #{code} 
   AND SEQ = #{seq}
