mapper.insert:

	SELECT NVL(MAX(NO), 0) + 1 NO  FROM TB_BOARD

INSERT INTO TB_BOARD (NO
                    , TITLE
                    , CONTENT
                    , USER_ID
                    , REGDATE)
     VALUES (#{no}
           , #{title}
           , #{content}
           , #{userId,jdbcType=VARCHAR}
           , SYSDATE)

mapper.update:
UPDATE TB_BOARD SET 
		TITLE = #{title}
	  , CONTENT = #{content}
 WHERE NO = #{no}
